const ATTRIBUTES = {
    MEETING: {
        TYPE: 'cf.cplace.solution.okr.meeting',
        ATTR: {
            MEETING_TYPE: 'cf.cplace.solution.okr.meetingType'
        },
        MEETING_TYPE: {
