        case ATTRIBUTES.MEETING.MEETING_TYPE.WORKSHOP:
            layouts.push(ATTRIBUTES.LAYOUTS.WORKSHOP);
        break;
    default:
  }

  layouts.push(ATTRIBUTES.LAYOUTS.FILES);


let layoutConfig = {
    layouts: layouts,
    active: layouts[0],
  }

  return layoutConfig;
